func parseDirection(r rune) Direction {
	if r == '<' {
		return Left
	}
	return Right
}

func parseGale(s string) *Gale {
	g := &Gale{}
	for _, r := range s {
		g.pattern = append(g.pattern, parseDirection(r))
	}
	return g
}
